INSERT INTO article VALUES("48","F�d�ral Open N2 Indoor 29-30/10/2011 Nemours (FR)","<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"modules/galerie/picture/N2%20INDOOR%20NEMOURS_20111030_42.JPG\" alt=\"\" width=\"555\" height=\"368\" /></p>
